using UnityEngine;using Newtonsoft.Json;public class UnitConfig  {		/// <summary>		///单元		/// <summary>		public int id { get; set; }		/// <summary>		///名称		/// <summary>		public int nameid { get; set; }		/// <summary>		///目录id		/// <summary>		public int[] directory { get; set; }		/// <summary>		///参与计算的数字		/// <summary>		public int[] count { get; set; }		/// <summary>		///最大得数或最小得数		/// <summary>		public int maxScore { get; set; }		 		public static string configName = "UnitConfig";		public static string Version { get { return Config.version; } }		public static UnitConfig [] Datas { get { return Config.datas; } }		public static UnitConfig Config {get { if (config == null) Init(); return config;}}		private static UnitConfig config;		public string version { get; set; }		public UnitConfig [] datas { get; set; }		private static void Init()		{			TextAsset jsonStr = Resources.Load("Config/"+configName) as TextAsset;			config = JsonConvert.DeserializeObject<UnitConfig>(jsonStr.text);		}		public static UnitConfig Get(int id)		{			foreach (var item in Config.datas)			{				if (item.id == id)				{ 					return item;				}			}			return null;		}		}