using UnityEngine;using Newtonsoft.Json;public class CategoryConfig  {		/// <summary>		///类目		/// <summary>		public int id { get; set; }		/// <summary>		///名称		/// <summary>		public int nameid { get; set; }		/// <summary>		///目录id		/// <summary>		public int[] directory { get; set; }		 		public static string configName = "CategoryConfig";		public static string Version { get { return Config.version; } }		public static CategoryConfig [] Datas { get { return Config.datas; } }		public static CategoryConfig Config {get { if (config == null) Init(); return config;}}		private static CategoryConfig config;		public string version { get; set; }		public CategoryConfig [] datas { get; set; }		private static void Init()		{			TextAsset jsonStr = Resources.Load("Config/"+configName) as TextAsset;			config = JsonConvert.DeserializeObject<CategoryConfig>(jsonStr.text);		}		public static CategoryConfig Get(int id)		{			foreach (var item in Config.datas)			{				if (item.id == id)				{ 					return item;				}			}			return null;		}		}